<template>    <div class="container-card">        <div class="card">            <h1>Blih Web</h1>            <p>Please log in with your Epitech's logs.</p>            <div>                <p v-if="errors.length">                    <p >{{ errors }}</p>                <div  class="form-group">                    <label for="email" class="form-label">Login</label>                    <br>                    <input id="email" class="input-form" v-model="email" placeholder="Email">                </div>                <div  class="form-group">                    <label for="password" class="form-label">Password</label>                    <br>                    <input type="password" id="password" class="input-form" v-model="password" placeholder="Password">                </div>                <div class="form-group">                    <button class="btn"                            @click="logForm"                    >                        Log In                    </button>                </div>            </div>        </div>    </div></template><script>    import Api from "../api";    import store from "../store/store"    export default {        name: "Login",        data(){            return {                errors: "",                email: "",                password: ""            }        },        methods: {            logForm() {                Api().get("api/gettoken", {                    params: {                        email: this.email,                        password: this.password                    }                }).then(response => {                    store.dispatch("setToken", response.data.token);                    store.dispatch("setEmail", this.email);                    this.$router.push(this.$route.query.redirect || '/repositories');                }).catch(e => {                        console.log(e);                })            }        }    }</script><style scoped>    .btn {        font-size: 20px;        padding: .5em;        color: white;        background: #00d27f;        border: none;    }    .form-label {        font-size: 20px;    }    .form-group {        padding-top: 2em;        display: block;        max-width: 80%;    }    .form-group label {        padding-bottom: 100px;    }    .input-form {        height: 2em;        width: 80%;        font-size: 17px;    }    .container-card {        margin-right: auto;        margin-left: auto;        margin-top: 10%;        max-width: 60rem;        border: 5px solid #00d27f;    }    .card {        padding-bottom: 2em;        padding-top: 2em;        padding-left: 2em;        background: #23282e;        color: white;    }    h1 {        margin-block-start: 0 !important;    }</style>