<template>    <div>        <nav>           <ul>               <li><a href="/repositories">Blih Web</a></li>               <li style="float: right;"><a href="/logout">{{ username }}</a></li>           </ul>        </nav>        <div class="container-card">            <sweet-modal  ref="modal" :title="repoName">                <p><span style="font-weight: bold">UUID:</span> {{ repoUuid }}</p>                <div  v-if="acls !== null" id="acls">                    <p><span style="font-weight: bold">ACLs:</span></p>                    <div class="div-form" v-for="(acl, index) in acls" :key="index">                        <!-- Bancale -->                        <input type="text" class="input-form" v-bind:value="acl.name">                        <checkbox  v-if="rights[index][0] === 'r'" checked class="checkbox-rights" name="terms">r</checkbox>                        <checkbox  v-else class="checkbox-rights" name="terms">r</checkbox>                        <checkbox  v-if="rights[index][1] === 'w'" checked class="checkbox-rights" name="terms">r</checkbox>                        <checkbox  v-else class="checkbox-rights" name="terms">w</checkbox>                        <checkbox  v-if="rights[index][2] === 'a'" checked class="checkbox-rights" name="terms">r</checkbox>                        <checkbox  v-else class="checkbox-rights" name="terms">a</checkbox>                    </div>                </div>                <p v-else>Loading ACLs...</p>                <sweet-button slot="button" color="red" v-on:click="deleteRepo(repoName)">                    <button class="btn success">Save</button>                    <button class="btn danger">Delete</button>                </sweet-button>            </sweet-modal>            <div v-if="repositories != null" class="content">                <ul class="tab-elem">                    <li                            v-for="(item, index) in repositories" :key="index"                            :style="{ 'background-color': color[index % 2]}"                    >                        <div v-if="item !== undefined">                            <a @click="open(item.name, item.uuid)">{{ item.name }}</a>                        </div>                    </li>                </ul>            </div>        </div>        <div v-if="repositories == null" style="text-align: center;">            <p>Loading...</p>        </div>    </div></template><script>    import Api from "../api"    import store from "../store/store"    import { SweetModal, SweetModalTab, SweetButton } from 'sweet-modal-vue'    import {Checkbox} from 'vue-checkbox-radio';    export default {        name: "Repositories",        components: {            SweetModal,            SweetModalTab,            SweetButton,            Checkbox,        },        data() {            return {                repositories: null,                email: store.state.email,                username: "",                index: 0,                color: ["#333", "#606060"],                border: ["5px solid #333 !important", "5px solid #606060 !important"],                repoItem: null,                repoName: "",                repoUuid: null,                acls: null,                rights: []            }        },        methods: {            deleteRepo(name) {                console.log(name);            },            open: function (name, uuid) {                this.repoUuid = uuid;                this.repoName = name;                Api().get("api/acls", {                    params: {                        email: this.email,                        token: store.state.token,                        repo: this.repoName                    }                }).then(response => {                    this.acls = response.data.data;                    for (let i = 0; i < response.data.data.length; i++) {                        let tab = [];                        for (let a = 0; a < response.data.data[i].rights.length; a++) {                            tab.push(response.data.data[i].rights[a]);                        }                        this.rights.push(tab);                    }                }).catch(e => {                    console.log(e);                });                this.$refs.modal.open()            },            transformLogin() {                this.username += this.email[0].toUpperCase();                for (let i = 1; this.email[i] !== "@"; i++) {                    if (this.email[i] === ".") {                        this.username += " ";                        this.username += this.email[i + 1].toUpperCase();                        i+=2;                    }                    this.username += this.email[i];                }            },            showEdit(color) {                if (color === "5px solid #333 !important") {                    this.border[0].replace("5px solid  #19cf86 !important");                }                else if (color === "5px solid #606060 !important")                    this.border[1] = "5px solid  #19cf86 !important";            }        },        created() {            this.transformLogin();            Api().get('api/repositories', {                params: {                    email: store.state.email,                    token: store.state.token,                    username: ""                }            }).then(response => {                this.repositories = response.data.repositories;            }).catch(e => {                console.log(e);            })        }    }</script><style scoped>    body {        background: #eee;    }    .checkbox-rights {        padding-right: 1em;    }    .div-form {        display: flex;        padding-bottom: 1em;    }    .input-form {        height: 2em;        width: 50%;        font-size: 17px;    }    .total-content {        display: block;        float: left;        color: black;        padding: 2em;        font-size: 15px;    }    .header-content {        display: block;        color: black;        padding: 2em;        font-size: 20px;    }    .header-content p{    }    .header-content-sub {        display: block;        color: black;        padding-bottom: 4em;        font-size: 20px;    }    .center {        text-align: center !important;    }    .left {        float: left;    }        .right {        float: right;    }        .container-card {        margin-right: auto;        margin-left: auto;        max-width: 60rem;        box-shadow: 1px 1px 12px #555;    }    .content {        margin: 0;        color: white;    }    nav {        padding-bottom: 2em;    }    nav ul {        list-style-type: none;        margin: 0;        overflow: hidden;        background-color: #333;        padding: 2em;    }    nav li {        float: left;    }    nav li a {        display: block;        color: white;        text-decoration: none;    }    .tab-elem ul {        background-color: #333;    }    .tab-elem li {        list-style-type: none  !important;    }    .tab-elem li a {        display: block;        color: white;        padding: 14px 16px;        text-decoration: none;    }    .tab-elem li:hover {        border-left: 5px solid  #19cf86 !important;    }    ul {        padding: 0;        /*margin-block-start: 0 !important;*/    }</style>