<template>    <div>        <nav>           <ul>               <li><a href="/repositories">Blih Web</a></li>               <li style="float: right;"><a href="/logout">{{ email }}</a></li>           </ul>        </nav>        <div class="container-card">            <div v-if="repositories != null" class="content">                <div v-if="repositories != null" class="header-content">                    <p class="left">Left</p>                    <p class="right">Right</p>                    <p class="center">Total repositories: {{ repositories.length }} repositories</p>                </div>                <ul class="tab-elem">                    <li                            v-for="(item, index) in repositories" :key="index"                            :style="{ 'background-color': color[index % 2]}"                    >                        <a :href="item.url">{{ item.name }}</a>                    </li>                </ul>            </div>            <div v-else-if="repositories == null" style="text-align: center;">                <p>Loading...</p>            </div>        </div>    </div></template><script>    import Api from "../api"    import store from "../store/store"    export default {        name: "Repositories",        data() {            return {                repositories: null,                email: store.state.email,                index: 0,                color: ["#333", "#606060"]            }        },        created() {            Api().get('api/repositories', {                params: {                    email: store.state.email,                    token: store.state.token                }            }).then(response => {                this.repositories = response.data.repositories;            }).catch(e => {                console.log(e);            })        }    }</script><style scoped>    body {        background: #eee;    }    .total-content {        display: block;        float: left;        color: black;        padding: 2em;        font-size: 15px;    }    .header-content {        display: block;        color: black;        padding: 2em;        font-size: 20px;    }    .center {        text-align: center !important;    }    .left {        float: left;    }        .right {        float: right;    }        .container-card {        margin-right: auto;        margin-left: auto;        max-width: 60rem;        box-shadow: 1px 1px 12px #555;    }    .content {        margin: 0;        color: white;    }    nav {        padding-bottom: 2em;    }    nav ul {        list-style-type: none;        margin: 0;        overflow: hidden;        background-color: #333;        padding: 2em;    }    nav li {        float: left;    }    nav li a {        display: block;        color: white;        text-decoration: none;    }    .tab-elem ul {        background-color: #333;    }    .tab-elem li {        list-style-type: none  !important;    }    .tab-elem li a {        display: flex;        color: white;        text-align: center;        padding: 14px 16px;        text-decoration: none;    }    .tab-elem li:hover {        border-left: 3px solid  #19cf86 !important;        padding-left: -3px;    }    ul {        padding: 0;    }</style>