<template>    <div>        <nav>           <ul>               <li><a href="/repositories">Blih Web</a></li>               <li style="float: right;"><a href="/logout">{{ username }}</a></li>           </ul>        </nav>        <div class="container-card">            <div v-if="repositories != null" class="content">                <div v-if="repositories != null" class="header-content">                    <p class="left">Left</p>                    <p class="right">Right</p>                </div>                <ul class="tab-elem">                    <li                            v-for="(item, index) in repositories" :key="index"                            :style="{ 'background-color': color[index % 2], 'border-left': border[index % 2]}"                            @mouseover="showEdit(border[index % 2])"                    >                        <div>                            <a :href="item.url">{{ item.name }}</a>                        </div>                    </li>                </ul>            </div>        </div>        <div v-if="repositories == null" style="text-align: center;">            <p>Loading...</p>        </div>    </div></template><script>    import Api from "../api"    import store from "../store/store"    export default {        name: "Repositories",        data() {            return {                repositories: null,                email: store.state.email,                username: "",                index: 0,                color: ["#333", "#606060"],                border: ["5px solid #333 !important", "5px solid #606060 !important"]            }        },        methods: {            transformLogin() {                this.username += this.email[0].toUpperCase();                for (let i = 1; this.email[i] !== "@"; i++) {                    if (this.email[i] === ".") {                        this.username += " ";                        this.username += this.email[i + 1].toUpperCase();                        i+=2;                    }                    this.username += this.email[i];                }            },            showEdit(color) {                console.log(color);                if (color === "5px solid #333 !important") {                    this.border[0].replace("5px solid  #19cf86 !important");                }                else if (color === "5px solid #606060 !important")                    this.border[1] = "5px solid  #19cf86 !important";            }        },        created() {            this.transformLogin();            Api().get('api/repositories', {                params: {                    email: store.state.email,                    token: store.state.token,                    username: ""                }            }).then(response => {                this.repositories = response.data.repositories;            }).catch(e => {                console.log(e);            })        }    }</script><style scoped>    body {        background: #eee;    }    .total-content {        display: block;        float: left;        color: black;        padding: 2em;        font-size: 15px;    }    .header-content {        display: block;        color: black;        padding: 2em;        font-size: 20px;    }    .header-content p{    }    .header-content-sub {        display: block;        color: black;        padding-bottom: 4em;        font-size: 20px;    }    .center {        text-align: center !important;    }    .left {        float: left;    }        .right {        float: right;    }        .container-card {        margin-right: auto;        margin-left: auto;        max-width: 60rem;        box-shadow: 1px 1px 12px #555;    }    .content {        margin: 0;        color: white;    }    nav {        padding-bottom: 2em;    }    nav ul {        list-style-type: none;        margin: 0;        overflow: hidden;        background-color: #333;        padding: 2em;    }    nav li {        float: left;    }    nav li a {        display: block;        color: white;        text-decoration: none;    }    .tab-elem ul {        background-color: #333;    }    .tab-elem li {        list-style-type: none  !important;    }    .tab-elem li a {        display: block;        color: white;        padding: 14px 16px;        text-decoration: none;    }    .tab-elem li:hover {        border-left: 5px solid  #19cf86 !important;    }    ul {        padding: 0;        /*margin-block-start: 0 !important;*/    }</style>